db:select.db:parameterized-query:
select manager_id, manager.user_id, user.first_name, user.last_name, user.email, user.phone, user.role
from manager inner join user on (manager.user_id = user.user_id)
where manager_id = #[flowVars.id]

select manager_id, manager.user_id, user.first_name, user.last_name, user.email, user.phone, user.role
from manager inner join user on (manager.user_id = user.user_id)
where user.first_name like #[flowVars.name]
