db:select.db:parameterized-query:
select worker_id, ucin, active, start_date, end_date, worker.user_id, user.first_name, user.last_name, user.email, user.phone, user.role
from worker inner join user on (worker.user_id = user.user_id)
where ucin like #[flowVars.ucin]

select worker_id, ucin, active, start_date, end_date, worker.user_id, user.first_name, user.last_name, user.email, user.phone, user.role
from worker inner join user on (worker.user_id = user.user_id)
where user.first_name like #[flowVars.name]
